Source code in <code>src/onesecondtrader/connectors/gateways/ib.py</code> <pre><code>async def _reconnect_async(self) -&gt; None:
    if self._reconnecting:
        return
    self._reconnecting = True

    while self._should_reconnect:
        self._reconnect_attempts += 1
        if (
            self._max_reconnect_attempts &gt; 0
            and self._reconnect_attempts &gt; self._max_reconnect_attempts
        ):
            _logger.error(
                "Max reconnect attempts (%d) reached, giving up",
                self._max_reconnect_attempts,
            )
            self._reconnecting = False
            return

        _logger.info(
            "Reconnecting to IB (attempt %d) in %.1fs",
            self._reconnect_attempts,
            self._reconnect_delay,
        )
        await asyncio.sleep(self._reconnect_delay)

        if not self._should_reconnect:
            break

        try:
            await self._ib.connectAsync(
                self._host, self._port, clientId=self._client_id
            )
            _logger.info("Reconnected to IB")
            self._reconnect_attempts = 0
            self._reconnecting = False

            for callback in self._reconnect_callbacks:
                try:
                    callback()
                except Exception:
                    _logger.exception("Error in reconnect callback")
            return
        except Exception as e:
            _logger.warning("Reconnect failed: %s", e)
            continue

    self._reconnecting = False
